$(document).ready(function () { $('#taskBtn').click(function (e) { e.preventDefault(); $('.task_section').show(); $('.user_section').hide() }); $('#userBtn').click(function (e) { e.preventDefault(); $('.user_section').show(); $('.task_section').hide() }); $(".toggle-password").click(function () { $(this).toggleClass("zmdi-eye zmdi-eye-off"); let input = $($(this).attr("toggle")); if (input.attr("type") == "password") { input.attr("type", "text") } else { input.attr("type", "password") } }) })